import React from "react";

class WordEntry extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            word: null,
            phonetic: null
        }
    }

    setupWordLesson(data) {
        this.setState({
            word: data[0]["word"],
            phonetic: data[0]["phonetic"]
        })
    }

    getMeanings(x) {
        let meanings = [];
        for (let i = 0; i < x; i++) {
            meanings.push(<h1>word</h1>);
        }
        return meanings;
    }

    render() {
        return (
            <div>
                <h2>{this.state.word}</h2>
                <h4>{this.state.phonetic}</h4>
                <div>
                    {this.getMeanings(10)}
                </div>
            </div>
        );
    }
}

export default WordEntry;
