import React from 'react';
import './WordEntry.css'

class WordEntry extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            word: null,
            phonetic: null,
            definitions: []
        }
    }

    setupWordLesson(data) {
        this.setState({
            word: data[0]["word"],
            phonetic: data[0]["phonetic"],
            definitions: this.parseDefinitions(data)
        })

        console.log(data);
    }

    parseDefinitions(meaningsData) {
        let definitions = [];

        console.log(meaningsData);

        for(let a = 0; a < meaningsData.length; a++) {
            for (let i = 0; i < meaningsData.length; i++) {
                let defs = [];

                for (let j = 0; j < meaningsData[a]["meanings"][i]["definitions"].length; j++) {
                    defs.push(meaningsData[a]["meanings"][i]["definitions"][j]["definition"]);
                }

                definitions.push({partOfSpeech: meaningsData[a]["meanings"][i]["partOfSpeech"], meanings: defs});
            }
        }

        return definitions;
    }

    getDefinitions() {
        let definitions = [];

        for (let i = 0; i < this.state.definitions.length; i++) {
            definitions.push((<h4>{this.state.definitions[i]["partOfSpeech"]}</h4>));

            for(let j = 0; j < this.state.definitions[i]["meanings"].length; j++) {
                definitions.push((<h6>{this.state.definitions[i]["meanings"][j]}</h6>));
            }
        }

        return definitions;
    }

    render() {
        return (
            <div>
                {this.state.word != null &&
                    <div id="lesson">
                        <h2>{this.state.word} â€¢ /{this.state.phonetic}/</h2>
                        <div>{this.getDefinitions()}</div>
                    </div>
                }
            </div>
        );
    }
}

export default WordEntry;
