import React from 'react';
import './WordEntry.css'

class WordEntry extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            word: null,
            phonetic: null,
            definitions: []
        }
    }

    setupWordLesson(data) {
        this.setState({
